import React, { useState } from 'react';
import Sound from 'react-sound';
import PreludeDiscoveries from '../../../public/ff-XIV-prelude-discoveries.mp3';

function BgSound({ handleSongLoading, handleSongPlaying, handleSongFinishedPlaying }) {

    const [isPlaying, setIsPlaying] = useState(false);

    return (
        <div>
            <button onClick={() => setIsPlaying(!isPlaying)}>{!isPlaying ? 'Play' : 'Stop'}</button>
            <Sound 
                url={PreludeDiscoveries}
                playStatus={ isPlaying ? Sound.status.PLAYING : Sound.status.STOPPED }
                onLoading={handleSongLoading}
                onPlaying={handleSongPlaying}
                onFinishedPlaying={handleSongFinishedPlaying}
            />
        </div>

    );
}

export default BgSound;
